class Solution:
    def findBottomLeftValue(self, root: Optional[TreeNode]) -> int:
        self.res = root.val
        self.max_depth = 0

        def helper(root, depth):
            if not root:
                return
            elif not root.left and not root.right:
                if depth > self.max_depth:
                    self.res = root.val
                    self.max_depth = depth
                    return 
