class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:

        def isPermutation(subs, ct, wl):

            ctsub = collections.defaultdict(int)
            for p in range(0, len(subs)-wl+1, wl):
                ctsub[subs[p: p+wl]] += 1

            if len(ctsub) != len(ct):
                return False

            for key in ct:
                if key not in ctsub or ctsub[key] != ct[key]:
                    return False

            return True


        wl = len(words[0])
        l = len(words)
        if wl*l > len(s):
            return []

        ct = collections.defaultdict(int)
        for w in words:
            ct[w] += 1

        res = []
        for i in range(len(s)-wl*l+1):
            if isPermutation(s[i:i+l*wl], ct, wl):
                res.append(i)
        return res
