import math
class Solution:
    def numberOfRounds(self, loginTime: str, logoutTime: str) -> int:
        loginHour, loginMin = [int(part) for part in loginTime.split(':')]
        logoutHour, logoutMin = [int(part) for part in logoutTime.split(':')]
        if loginHour > logoutHour or (loginHour == logoutHour and loginMin > logoutMin):
            return self.numberOfRounds(loginTime, "24:00") + self.numberOfRounds("00:00", logoutTime)
        loginHour, loginMin = self.findNextClosetCutoff(loginHour, loginMin)
        logoutHour, logoutMin = self.findPrevClosetCutoff(logoutHour, logoutMin)
        if loginHour > logoutHour:
            return 0
        elif loginHour == logoutHour:
            if loginMin >= logoutMin:
                return 0
            return (logoutMin - loginMin) // 15
        else:
            return (logoutHour - loginHour) * 4 + logoutMin // 15 - loginMin // 15

    def findNextClosetCutoff(self, loginHour, loginMin):
        cutoff = [0, 15, 30, 45]
        i = math.ceil(loginMin / 15) 
        if i >= len(cutoff):
            return loginHour+1, cutoff[i%len(cutoff)]
        else:
            return loginHour, cutoff[i]

    def findPrevClosetCutoff(self, logoutHour, logoutMin):
        cutoff = [0, 15, 30, 45]
        i = math.floor(logoutMin / 15) 
        return logoutHour, cutoff[i]
