        if len(s) < len(p):
            return []
        pct = [0]*26
        for c in p:
            pct[ord(c)-ord('a')] += 1
        ct = [0]*26
        for i in range(len(p)):
            if s[i] in p:
                ct[ord(s[i])-ord('a')] += 1
        res = []
