            res.append(0)
        start = 0
        for end in range(len(p), len(s)):
            if s[end] in p:
                ct[ord(s[end])-ord('a')] += 1
            if s[start] in p:
                ct[ord(s[start])-ord('a')] -= 1
            start += 1
            if self.isAnagram(pct, ct):
                res.append(start)
        return res

    def isAnagram(self, pct, ct):
        for i in range(26):
            if pct[i] != ct[i]:
