class Solution {
    public int minSubarray(int[] nums, int p) {
        long sum = 0L;
        for (var num : nums) sum += num;
        int mod = (int) ((sum % p + p) % p);

        if (mod == 0) return 0;

        int n = nums.length;
        int ans = n;
        sum = 0L;

        Map<Integer, Integer> map = new HashMap<>();
        map.put(0 , -1);

        for (int i = 0; i < n; i++) {
            sum += nums[i];
            int curM = (int) ((sum % p + p) % p);
            map.put(curM, i);

            int targetM = curM - mod + (curM >= mod ? 0 : p);
            if (map.containsKey(targetM))
                ans = Math.min(ans, i - map.get(targetM));
        }

        return ans == n ? -1 : ans;
    }
}
