class Solution(object):
    def verticalTraversal(self, root):
        if not root:
            return []

        min_col, max_col = 0, 0
        q = collections.deque()
        items = collections.defaultdict(list)
        q.append((root, 0, 0))

        while q:
            node, row, col = q.popleft()
            if col < min_col:
                min_col = col
            if col > max_col:
                max_col = col

            items[col].append((node.val, row))

            if node.left:
                q.append((node.left, row + 1, col - 1))
            if node.right:
                q.append((node.right, row + 1, col + 1))

        res = []
        for i in range(min_col, max_col + 1):
            item = items[i]
            item.sort(key = lambda x: (x[1], x[0]))
            res.append([x[0] for x in item])

        return res

