class Solution(object):
    def findSubstring(self, s, words):
        word_length = len(words[0])
        word_n = len(words)
        word_count = collections.Counter(words)
        n = len(s)        

        def helper(i):
            word_copy = word_count.copy()
            used = 0
            for j in range(i, i + word_length * word_n, word_length):
                sub = s[j : j + word_length]
                if word_copy[sub] >= 1:
                    word_copy[sub] -= 1
                    used += 1
                else:
                    break

            return used == word_n # word_count.total()

        ans = []
        for i in range(n - word_n * word_length + 1):
            if helper(i):
                ans.append(i)

        return ans


