class Solution(object):
    def sortItems(self, n, m, group, beforeItems):
        def assignGroupIds():
            group_id = m
            for i in range(n):
                if group[i] == -1:
                    group[i] = group_id
                    group_id += 1
            return group_id

        def buildGraph(group_id):
            item_graph = defaultdict(list)
            group_graph = defaultdict(list)
            item_indegree = [0] * n
            group_indegree = [0] * group_id

            for i in range(n):
                for prereq in beforeItems[i]:
                    item_graph[prereq].append(i)
                    item_indegree[i] += 1
                    if group[prereq] != group[i]:
                        group_graph[group[prereq]].append(group[i])
                        group_indegree[group[i]] += 1

            return item_graph, group_graph, item_indegree, group_indegree

        def topologicalSort(graph, indegree):
            res = []
            q = deque()
            for i in range(len(indegree)):
                if indegree[i] == 0:
                    q.append(i)

            while q:
                current = q.popleft()
                res.append(current)
                for neighbour in graph[current]:
                    indegree[neighbour] -= 1
                    if indegree[neighbour] == 0:
                        q.append(neighbour)

            return res if len(res) == len(indegree) else []

        # step 1: assign group id to items that do not belong to any group
        group_id = assignGroupIds()

        # step 2: build item and group dependency graph and indegree
        item_graph, group_graph, item_indegree, group_indegree = buildGraph(group_id)

        # step 3: perform topological sort for both item and group
        item_sort = topologicalSort(item_graph, item_indegree)
        group_sort = topologicalSort(group_graph, group_indegree)
        if not item_sort or not group_sort:
            return []

        # step 4: sort items in each group
        ordered_group = defaultdict(list)
        for item in item_sort:
            ordered_group[group[item]].append(item)

        # step 5: concat sorted items in each group by group order
        final_order = []
        for group in group_sort:
            final_order.extend(ordered_group[group])

        return final_order


