class Solution {
    public int maxChunksToSorted(int[] arr) {
        // i
        // [0...i](maxFromLeft)  [i+1...n-1](minFromRight)
        // maxFromLeft > minFromRightmaxFromLeft

        int n = arr.length;
        int[] maxFromLeft = new int[n];
        int[] minFromRight = new int[n+1];

        maxFromLeft[0] = arr[0];
        for (int i=1; i<n; i++) {
            maxFromLeft[i] = Math.max(maxFromLeft[i-1], arr[i]);
        }

        minFromRight[n] = 0x3f3f3f;
        for (int i=n-1; i>=0; i--) {
            minFromRight[i] = Math.min(minFromRight[i+1], arr[i]);
        }

        int count = 1;
        for (int i=0; i<n-1; i++) {
            if (maxFromLeft[i] <= minFromRight[i+1]) {
                count++;
            }
        }

        return count;
    }
}
