class Solution {
    TreeMap<Integer, TreeMap<Integer, List<Integer>>> map; // {col_index -> {row_index -> [val1, val2....valn]}}
    public List<List<Integer>> verticalTraversal(TreeNode root) {
        map = new TreeMap<>(); // SC O(N)
        dfs(root, 0, 0); // TC O(N), SC O(logN)
        
        List<List<Integer>> ans = new ArrayList<>();
        for (int col : map.keySet()) { // TC O(X)
            List<Integer> list = new ArrayList<>();
            TreeMap<Integer, List<Integer>> rowVals = map.get(col);
            for (int row : rowVals.keySet()) { // TC O(Y)
                List<Integer> vals = rowVals.get(row);
                Collections.sort(vals); // TC O(K * logK)
                list.addAll(vals);
            }

            ans.add(list);
        }
        
        return ans;
    }
    
    private void dfs(TreeNode root, int col, int row) {
        if (root == null) {
            return;
        }
        
        // TC O(X * LogX) + O(Y * LogY)
        map.computeIfAbsent(col, k -> new TreeMap<>()).computeIfAbsent(row, k -> new ArrayList<>()).add(root.val);
        dfs(root.left, col - 1, row + 1);
        dfs(root.right, col + 1, row + 1);
    }
}
