class Solution {
    public int numberOfBoomerangs(int[][] points) {
        int n = points.length;
        int total = 0;
        for (int i=0; i<n; i++) { // TC O(N^2)
            int x = points[i][0];
            int y = points[i][1];
            Map<Integer, Integer> distCnt = new HashMap<>(); // i
            for (int j=0; j<n; j++) {
                if (i == j) {
                    continue;
                }
                int x1 = points[j][0];
                int y1 = points[j][1];
                int dist = (x - x1) * (x - x1) + (y - y1) * (y - y1);
                distCnt.put(dist, distCnt.getOrDefault(dist, 0) + 1); // SC O(N)
            }
            
            // niin2 = n * (n-1)
            for (int count : distCnt.values()) {
                total += count * (count - 1);
            }
        }
        
        return total;
    }
}
