class Solution {
    public int maxScore(int[] points, int k) {
        long totalSum = 0;
        for (int p : points) {  // TC O(n)
            totalSum += p;
        }

        int sizeOfWindow = points.length - k;
        if (sizeOfWindow == 0) {
            return (int)totalSum;
        }
        
        // fixed-length sliding window
        long sumOfWindow = 0;
        for (int i=0; i<sizeOfWindow; i++) {
            sumOfWindow += points[i];
        }
        
        long minSumOfWindow = sumOfWindow;
        for (int i=sizeOfWindow; i < points.length; i++) { // TC O(n)
            sumOfWindow -= points[i - sizeOfWindow]; // left
            sumOfWindow += points[i]; // right
            minSumOfWindow = Math.min(minSumOfWindow, sumOfWindow);
        }
        
        return (int)(totalSum - minSumOfWindow);
    }
}
