class Solution {
    int[][] directions = {{1, 0}, {-1, 0}, {0, 1}, {0, -1}};
    int m = 0;
    int n = 0;
    public int maxAreaOfIsland(int[][] grid) {
        m = grid.length;
        n = grid[0].length;

        int maxArea = 0;
        for (int i=0; i<m; i++) {
            for (int j=0; j<n; j++) {
                if (grid[i][j] == 1) {
                    maxArea = Math.max(maxArea, dfs(grid, i, j));
                }
            }
        }

        return maxArea;
    }

    private int dfs(int[][] grid, int startX, int startY) {
        int count = 1;
        grid[startX][startY] = 0;
        // 
        for (int[] d : directions) {
            int nextX = startX + d[0];
            int nextY = startY + d[1];
            if (isValid(grid, nextX, nextY)) {
                count += dfs(grid, nextX, nextY);
            }
        }

        return count;
    }

    private boolean isValid(int[][] grid, int x, int y) {
        return x >= 0 && x < m && y >= 0 && y < n && grid[x][y] == 1;
    }
}
