class Solution {
    public int findNumberOfLIS(int[] nums) {
        int n = nums.length;
        int[] dp = new int[n]; // dp(i): nums[i]
        int[] counts = new int[n];
        int maxLen = 0;
        int total = 0;
        for (int i=0; i<n; i++) { // TC O(n^2)
            dp[i] = 1;
            counts[i] = 1;
            for (int j=0; j<i; j++) {
                if (nums[i] > nums[j]) {
                    if (dp[j] + 1 > dp[i]) {
                        dp[i] = dp[j] + 1;
                        counts[i] = counts[j];
                    } else if (dp[j] + 1 == dp[i]) {
                        counts[i] += counts[j];
                    }
                }
            }

            if (dp[i] > maxLen) {
                maxLen = dp[i];
                total = counts[i];
            } else if (dp[i] == maxLen) {
                total += counts[i];
            }
        }

        return total;
    }
}
