class Solution {
    Integer[] memo;
    public int coinChange(int[] coins, int amount) {
        memo = new Integer[amount + 1]; // SC O(n)
        return dfs(coins, amount);
    }

    private int dfs(int[] coins, int amount) {
        if (amount == 0) {
            return 0;
        }

        if (memo[amount] != null) { // TC O(n)
            return memo[amount];
        }

        // try every coin
        int minTotal = amount + 1;
        for (int i=0; i<coins.length; i++) {
            int coin = coins[i];
            if (amount - coin >= 0) {
                int total = dfs(coins, amount - coin);
                if (total != -1) {
                    minTotal = Math.min(minTotal, total + 1);
                }
            }
        }

        memo[amount] = (minTotal == amount + 1 ? -1 : minTotal);
        return memo[amount];
    }
}
