class Solution {
    public int makeConnected(int n, int[][] connections) {
        int total = connections.length;
        if (total < n - 1) {
            // we need at least n - 1 edges to connect all nodes
            return -1;
        }

        // get the number of connected componets in the graph
        int totalComps = n;
        int[] roots = new int[n];
        for (int i=0; i<n; i++) {
            roots[i] = i;
        }

        for (int[] con : connections) {
            int root1 = findRoot(roots, con[0]);
            int root2 = findRoot(roots, con[1]);
            if (root1 != root2) {
                // union
                roots[root1] = root2;
                totalComps--;
            }
        }

        // we need to connect these connected components togeter into one
        // so we need to move the edges within the components to connect all componets
        return totalComps - 1;
    }

    private int findRoot(int[] roots, int x) {
        while (x != roots[x]) {
            x = roots[x];
        }

        return x;
    }
}
