var rotateRight = function(head, k) {
    if(head === null || head.next === null) return head
    let tmp = head;
    let count = 1
    let arr = []
    while( tmp.next){
        tmp = tmp.next
        count ++
    }
    let shiftNum = k% count;
    if(shiftNum === 0){
        return head
    }
    tmp = head
    while( tmp.next){
        if(arr.length === shiftNum){
            arr.shift()
        }
        arr.push(tmp)
        tmp = tmp.next
        count ++
    }
    arr.push(tmp)
    arr[0].next = null

    arr[arr.length-1].next = head
    return arr[1]

};
