function buildTreeFn (left,right){
    if(left === right){
        return null
    }
    let fast = left;
    let slow = left;
    while(fast !== right && fast.next !== right){
        fast = fast.next.next
        slow = slow.next
    }
    let leftTree = buildTreeFn(left,slow)
    let rightTree = buildTreeFn(slow.next,right)
    return new TreeNode(slow.val,leftTree,rightTree)
}

