double new21Game(int N, int K, int W) {
    if (!K) 
       return 1.0;
    double* dp = (double*)malloc(sizeof(double) * (K + W));
    for (int i = K; i < K + W; i++) {
        if (i < N + 1)
            dp[i] = 1.0;
        else
            dp[i] = 0.0;
    }
    double sum = K + W < N + 1 ? 1.0 : (double)(N + 1 - K) / W;
    for (int i = K - 1; i >= 0; i--) {
        if (i < K - 1) sum += (dp[i + 1] - dp[i + W + 1]) / W;
        dp[i] = sum;
    }
    return dp[0];
}
