using namespace std;

class Solution {
    public bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        unordered_map<int, vector<int>> graph;
        for (auto& edge : dislikes) {
            graph[edge[0]].push_back(edge[1]);
            graph[edge[1]].push_back(edge[0]);
        }
        vector<int> group(N+1, 0);
        for (int i = 1; i <= N; ++i) {
            if (group[i] == 0 && !dfs(i, 1, group, graph)) {
                return false;
            }
        }
        return true;
    }

    public bool dfs(int node, int grp, vector<int>& group, unordered_map<int, vector<int>>& graph) {
        if (group[node] == 0) {
            group[node] = grp;
        } else {
            return group[node] == grp;
        }
        if (graph.count(node)) {
            for (int neighbor : graph[node]) {
                if (!dfs(neighbor, -grp, group, graph)) {
                    return false;
                }
            }
        }
        return true;
    }
};

