        return 1.0;
    }

    vector<double> dp(N + 1);
    dp[0] = 1.0;
    double Wsum = 1.0, res = 0.0;

    for (int i = 1; i <= N; ++i) {
        dp[i] = Wsum / W;
        if (i < K) {
            Wsum += dp[i];
        } else {
            res += dp[i];
        }
        if (i - W >= 0) {
            Wsum -= dp[i - W];
