#include <vector>
#include <numeric>

class Solution {
public:
    int minMalwareSpread(std::vector<std::vector<int>>& graph, std::vector<int>& initial) {
        int n = graph.size();
        std::vector<int> parent(n), size(n, 1), malware(n, 0);
        std::iota(parent.begin(), parent.end(), 0);
        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                if (graph[i][j]) {
                    int root_i = find(i, parent), root_j = find(j, parent);
                    if (root_i != root_j) {
                        parent[root_i] = root_j;
                        size[root_j] += size[root_i];
                    }
                }
            }
        }
        for (int node : initial) {
            ++malware[find(node, parent)];
        }
        std::vector<int> res(2, 0);
        for (int node : initial) {
            int root = find(node, parent), m = malware[root], s = size[root];
            if ((m == 1 && s > res[0]) || (m == 1 && s == res[0] && node < res[1]) || m > 1 && res[0] == 1) {
                res = {s, node};
            }
        }
        return res[1];
    }

private:
    int find(int x, std::vector<int>& parent) {
        if (x != parent[x]) {
            parent[x] = find(parent[x], parent);
        }
        return parent[x];
    }
}; // O(n^2)
