var rotateRight = function(head, k) {
    if (!head || !head.next) return head; // ,

    // 1. 
    let n = 0, curr = head;
    while (curr) {
        n++;
        curr = curr.next;
    }

    // 2. 
    k = k % n;
    if (k === 0) return head; // 

