    curr = head;
    for (let i = 0; i < n - k - 1; i++) {
        curr = curr.next;
    }
    let newHead = curr.next;

    // 4. 
    curr.next = null;
    curr = newHead;
    while (curr.next) {
        curr = curr.next;
    }
    curr.next = head;

    // 5. 
    return newHead;
};
