var swapPairs = function(head) {
    // ,
    if (!head || !head.next) {
        return head;
    }

    // ,
    let dummy = new ListNode(0);
    dummy.next = head;

    // :prev,curr,nextNode
    let prev = dummy;
    let curr = head;

    // ,
