var minSubarray = function(nums, p) {
    const n = nums.length;
    // 
    let need = 0;
    for (let num of nums) {
        need = (need + num) % p;
    }

    if (need === 0) return 0;

    // 
    const map = new Map();
    map.set(0, -1);
    let curr = 0;
    let minLen = n;

    for (let i = 0; i < n; i++) {
        curr = (curr + nums[i]) % p;
        // 
        let target = (curr - need + p) % p;

        if (map.has(target)) {
            minLen = Math.min(minLen, i - map.get(target));
        }
        map.set(curr, i);
    }

    return minLen === n ? -1 : minLen;
};
