    for (let i = 0; i < n; i++) {
        if (group[i] === -1) {
            group[i] = groupId++;
        }
    }

    // 
    const groupGraph = new Array(groupId).fill(0).map(() => []);
    const itemGraph = new Array(n).fill(0).map(() => []);
    const groupDegree = new Array(groupId).fill(0);
    const itemDegree = new Array(n).fill(0);

    // 
    for (let i = 0; i < n; i++) {
        const curGroup = group[i];
        for (const before of beforeItems[i]) {
            const beforeGroup = group[before];
            if (beforeGroup !== curGroup) {
                groupGraph[beforeGroup].push(curGroup);
                groupDegree[curGroup]++;
            }
            itemGraph[before].push(i);
            itemDegree[i]++;
        }
    }

    // 
    const topologicalSort = (graph, degree) => {
        const n = graph.length;
        const queue = [];
        const res = [];

        for (let i = 0; i < n; i++) {
            if (degree[i] === 0) {
                queue.push(i);
            }
        }

        while (queue.length) {
            const cur = queue.shift();
            res.push(cur);
            for (const next of graph[cur]) {
                degree[next]--;
                if (degree[next] === 0) {
                    queue.push(next);
                }
            }
        }

        return res.length === n ? res : [];
    };

    // 
    const groupOrdered = topologicalSort(groupGraph, groupDegree);
    if (!groupOrdered.length) return [];

    // 
    const ans = [];
    const groupItems = new Array(groupId).fill(0).map(() => []);

    for (let i = 0; i < n; i++) {
        groupItems[group[i]].push(i);
    }

    for (const curGroup of groupOrdered) {
        const items = groupItems[curGroup];
        const sorted = topologicalSort(
            itemGraph.map(x => x.filter(y => group[y] === curGroup)),
            itemDegree.map((x, i) => group[i] === curGroup ? x : 0)
        );
        if (sorted.length !== items.length) return [];
        ans.push(...sorted.filter(item => group[item] === curGroup));
    }

    return ans;
};
