 * @return {number[]}
 */
var getOrder = function(tasks) {
    const n = tasks.length;
    const indices = tasks.map((_, i) => i);
    indices.sort((a, b) => tasks[a][0] - tasks[b][0]);

    const result = [];
    const minHeap = new MinPriorityQueue({
        priority: x => tasks[x][1] * 1e9 + x
    });

    let i = 0;
    let time = tasks[indices[0]][0];

    while (result.length < n) {
        while (i < n && tasks[indices[i]][0] <= time) {
            minHeap.enqueue(indices[i++]);
        }

        if (minHeap.isEmpty()) {
            time = tasks[indices[i]][0];
            continue;
        }

        const idx = minHeap.dequeue().element;
        result.push(idx);
        time += tasks[idx][1];
    }

    return result;
};
