/**
 * @param {number[][]} grid
 * @return {number}
 * : O(N^2 * log(N)) 
 * : O(N^2) 
 */
var swimInWater = function(grid) {
    const n = grid.length;
    let left = grid[0][0];
    let right = n * n - 1;

    // 
    while (left < right) {
        const mid = Math.floor((left + right) / 2);
        const visited = new Array(n).fill(0).map(() => new Array(n).fill(false));

        if (canReach(grid, mid, 0, 0, visited, n)) {
            right = mid;
        } else {
            left = mid + 1;
        }
    }

    return left;
};

// DFS(0,0)(n-1,n-1)
function canReach(grid, time, i, j, visited, n) {
    if (i < 0 || i >= n || j < 0 || j >= n || visited[i][j] || grid[i][j] > time) {
        return false;
    }

    if (i === n-1 && j === n-1) {
        return true;
    }

    visited[i][j] = true;

    // DFS
    const directions = [[0,1], [1,0], [0,-1], [-1,0]];
    for (const [di, dj] of directions) {
        const ni = i + di;
        const nj = j + dj;
        if (canReach(grid, time, ni, nj, visited, n)) {
            return true;
        }
    }

    return false;
}
