/**
 * @param {string} s 
 * @param {string} t 
 * @return {string} 
 */
const minWindow = function(s, t) {
    // 
    if (s.length < t.length) return "";

    // 
    const need = {};
    for (let c of t) {
        need[c] = (need[c] || 0) + 1;
    }

    // 
    const needTypes = Object.keys(need).length;

    // 
    let valid = 0;
    // 
    const window = {};

    // 
    let start = 0;
    let minLen = Infinity;

    // 
    let left = 0;
    let right = 0;

    // 
    while (right < s.length) {
        // 
        let c = s[right];
        right++;

        // 
        if (need[c]) {
            window[c] = (window[c] || 0) + 1;
            if (window[c] === need[c]) {
                valid++;
            }
        }

        // 
        while (valid === needTypes) {
            // 
            if (right - left < minLen) {
                start = left;
                minLen = right - left;
            }

            // 
            let d = s[left];
            left++;

            // 
            if (need[d]) {
                if (window[d] === need[d]) {
                    valid--;
                }
                window[d]--;
            }
        }
    }

    return minLen === Infinity ? "" : s.substr(start, minLen);
};
