/**
 * @param {number} n
 * @return {number}
 * Time: O(N) 
 * Space: O(N)
 */
var totalNQueens = function(n) {
    let count = 0;
    const cols = new Set();
    const diag1 = new Set();  // r + c
    const diag2 = new Set();  // r - c

    const backtrack = (row) => {
        if (row === n) {
            count++;
            return;
        }

        for (let col = 0; col < n; col++) {
            if (cols.has(col) || 
                diag1.has(row + col) || 
                diag2.has(row - col)) {
                continue;
            }

            cols.add(col);
            diag1.add(row + col);
            diag2.add(row - col);

            backtrack(row + 1);

            cols.delete(col);
            diag1.delete(row + col);
            diag2.delete(row - col);
        }
    };

    backtrack(0);
    return count;
};
