/**
 * @param {number[]} status
 * @param {number[]} candies
 * @param {number[][]} keys
 * @param {number[][]} containedBoxes
 * @param {number[]} initialBoxes
 * @return {number}
 */
var maxCandies = function(status, candies, keys, containedBoxes, initialBoxes) {
    const n = status.length;
    // 
    const haveBoxes = new Set(initialBoxes);
    // 
    const haveKeys = new Set();
    for(let i = 0; i < n; i++) {
        if(status[i]) haveKeys.add(i);
    }

    // 
    const queue = initialBoxes.filter(box => haveKeys.has(box));
    // 
    const processed = new Set();
    // 
    let totalCandies = 0;

    while(queue.length) {
        const box = queue.shift();
        if(processed.has(box)) continue;

        processed.add(box);
        // 
        totalCandies += candies[box];

        // 
        for(const key of keys[box]) {
            haveKeys.add(key);
            // ,
            if(haveBoxes.has(key) && !processed.has(key)) {
                queue.push(key);
            }
        }

        // 
        for(const newBox of containedBoxes[box]) {
            haveBoxes.add(newBox);
            // ,
            if(haveKeys.has(newBox) && !processed.has(newBox)) {
                queue.push(newBox);
            }
        }
    }

    return totalCandies;
};
