/**
 * @param {number[]} nums
 * @return {number[][]}
 * Time: O(n! * n) - n!, O(n)
 * Space: O(n) - 
 */
function permuteUnique(nums) {
    const result = [];
    nums.sort((a, b) => a - b); // 

    function backtrack(used, curr) {
        if (curr.length === nums.length) {
            result.push([...curr]);
            return;
        }

        for (let i = 0; i < nums.length; i++) {
            // 
            if (used[i]) continue;

            // 
            if (i > 0 && nums[i] === nums[i-1] && !used[i-1]) continue;

            used[i] = true;
            curr.push(nums[i]);
            backtrack(used, curr);
            curr.pop();
            used[i] = false;
        }
    }

    backtrack(new Array(nums.length).fill(false), []);
    return result;
}
