/**
 * 
 * - search: O(log n) 
 * - add: O(log n) 
 * - erase: O(log n) 
 * O(n)
 */

var Skiplist = function() {
    this.MAX_LEVEL = 16; // 
    this.P = 0.5; // 
    this.level = 1; // 

    // 
    this.head = { val: -1, next: new Array(this.MAX_LEVEL).fill(null) };
};

/**
 * 
 * @return {number}
 */
Skiplist.prototype.randomLevel = function() {
    let level = 1;
    while (Math.random() < this.P && level < this.MAX_LEVEL) {
        level++;
    }
    return level;
};

/**
 * @param {number} target
 * @return {boolean}
 */
Skiplist.prototype.search = function(target) {
    let current = this.head;

    // 
    for (let i = this.level - 1; i >= 0; i--) {
        // 
        while (current.next[i] !== null && current.next[i].val < target) {
            current = current.next[i];
        }
    }

    //  current  target 
    current = current.next[0];

    // 
    return current !== null && current.val === target;
};

/**
 * @param {number} num
 * @return {void}
 */
Skiplist.prototype.add = function(num) {
    const update = new Array(this.MAX_LEVEL).fill(this.head);
    let current = this.head;

    // 
    for (let i = this.level - 1; i >= 0; i--) {
        while (current.next[i] !== null && current.next[i].val < num) {
            current = current.next[i];
        }
        update[i] = current;
    }

    // 
    const newLevel = this.randomLevel();

    // 
    if (newLevel > this.level) {
        for (let i = this.level; i < newLevel; i++) {
            update[i] = this.head;
        }
        this.level = newLevel;
    }

    // 
    const newNode = { val: num, next: new Array(newLevel).fill(null) };

    // 
    for (let i = 0; i < newLevel; i++) {
        newNode.next[i] = update[i].next[i];
        update[i].next[i] = newNode;
    }
};

/**
 * @param {number} num
 * @return {boolean}
 */
Skiplist.prototype.erase = function(num) {
    const update = new Array(this.MAX_LEVEL).fill(null);
    let current = this.head;

    // 
    for (let i = this.level - 1; i >= 0; i--) {
        while (current.next[i] !== null && current.next[i].val < num) {
            current = current.next[i];
        }
        update[i] = current;
    }

    // 
    current = current.next[0];

    //  false
    if (current === null || current.val !== num) {
        return false;
    }

    // 
    for (let i = 0; i < this.level; i++) {
        if (update[i].next[i] !== current) {
            break;
        }
        update[i].next[i] = current.next[i];
    }

    // 
    while (this.level > 1 && this.head.next[this.level - 1] === null) {
        this.level--;
    }

