impl Trie {
    fn new() -> Self {
        Self {
            root: Node::new()
        }
    }


    fn insert(&mut self, word: String) {
        let mut cur = &mut self.root;
        for c in word.bytes() {
            let idx = (c - b'a') as usize;
            assert!(idx < 26, "Invalid character '{}'", c as char);
            if cur.son[idx].is_none() {
                cur.son[idx] = Some(Box::new(Node::new()));
            }
            cur = cur.son[idx].as_mut().unwrap(); 
        }
        cur.end = true; 
    }


    fn find(&self, word: String) -> i32 {
        let mut cur = &self.root;
        for c in word.bytes() {
            let idx = (c - b'a') as usize;
            assert!(idx < 26, "Invalid character '{}'", c as char);
            match &cur.son[idx] {
                Some(node) => cur = node,
                None => return 0,
            }
        }
        if cur.end { 2 } else { 1 }
    }


    fn search(&self, word: String) -> bool {
        self.find(word) == 2
    }


    fn starts_with(&self, prefix: String) -> bool {
        self.find(prefix) != 0
    }
