impl Solution {
    pub fn last_stone_weight(stones: Vec<i32>) -> i32 {
        let mut h = BinaryHeap::from(stones);
        while h.len() > 1 {
            let d = h.pop().unwrap() - h.pop().unwrap();
            h.push(d);
        }
        h.pop().unwrap()
    }
