class Solution:
    def singleNumber(self, nums: List[int]) -> List[int]:
        '''
        Use O(n) space, O(n) time
        '''
        c = collections.Counter(nums)
        res = []
        for key, value in c.items():
            if value == 1:
                res.append(key)
        return res
